/**
 * Global Type Definitions for Storybook
 *
 * This file provides type definitions for global objects used in Storybook stories
 * including the Braintree SDK, PayPal SDK, and ApplePaySession.
 */

/* eslint-disable no-unused-vars */

// ============================================================================
// Braintree SDK Types
// NOTE: Most of these were generated by Claude with only some spot checking
// on their validity.
// ============================================================================

/**
 * Braintree gateway configuration returned by client.getConfiguration()
 */
interface IBraintreeConfiguration {
  authorization: string;
  authorizationType: string;
  environment: string;
  clientApiUrl: string;
  assetsUrl: string;
  configUrl: string;
  graphQL: {
    url: string;
    date: string;
    features: string[];
  };
  analytics: {
    url: string;
  };
  merchantId: string;
  venmo?: {
    accessToken: string;
    environment: string;
    merchantId?: string;
  };
  paypal?: {
    displayName?: string;
    clientId?: string;
    privacyUrl?: string;
    userAgreementUrl?: string;
    assetsUrl?: string;
    environment?: string;
    environmentNoNetwork?: boolean;
    unvettedMerchant?: boolean;
    braintreeClientId?: string;
    billingAgreementsEnabled?: boolean;
    merchantAccountId?: string;
    currencyIsoCode?: string;
  };
  paypalEnabled?: boolean;
  applePay?: {
    countryCode: string;
    currencyCode: string;
    merchantIdentifier: string;
    supportedNetworks: string[];
  };
  challenges?: string[];
  creditCards?: {
    supportedCardTypes: string[];
    supportedGateways?: Array<{
      name: string;
    }>;
  };
  threeDSecure?: {
    cardinalAuthenticationJWT?: string;
  };
  [key: string]: unknown;
}

/**
 * Braintree Client instance
 */
interface IBraintreeClient {
  getConfiguration(): { gatewayConfiguration: IBraintreeConfiguration };
  request(options: unknown): Promise<unknown>;
  teardown(): Promise<void>;
}

// ============================================================================
// Hosted Fields Types
// ============================================================================

/**
 * Field state information
 */
interface IHostedFieldState {
  container: HTMLElement;
  isFocused: boolean;
  isEmpty: boolean;
  isPotentiallyValid: boolean;
  isValid: boolean;
}

/**
 * Card type information
 */
interface ICardType {
  type: string;
  niceType: string;
  code: {
    name: string;
    size: number;
  };
  gaps: number[];
  lengths: number[];
  pattern: string | number;
}

/**
 * Hosted Fields state returned by getState()
 */
interface IHostedFieldsState {
  cards: ICardType[];
  fields: {
    number?: IHostedFieldState;
    cvv?: IHostedFieldState;
    expirationDate?: IHostedFieldState;
    expirationMonth?: IHostedFieldState;
    expirationYear?: IHostedFieldState;
    postalCode?: IHostedFieldState;
    cardholderName?: IHostedFieldState;
  };
}

/**
 * Hosted Fields tokenize payload
 */
interface IHostedFieldsTokenizePayload {
  nonce: string;
  type: string;
  details: {
    bin: string;
    cardType: string;
    expirationMonth: string;
    expirationYear: string;
    lastFour: string;
    lastTwo: string;
  };
  description: string;
  binData: {
    commercial: string;
    countryOfIssuance: string;
    debit: string;
    durbinRegulated: string;
    healthcare: string;
    issuingBank: string;
    payroll: string;
    prepaid: string;
    productId: string;
  };
}

/**
 * Hosted Fields event callback data
 */
interface IHostedFieldsEventData {
  emittedBy: string;
  fields: IHostedFieldsState["fields"];
  cards: ICardType[];
}

/**
 * Hosted Fields instance
 */
interface IHostedFieldsInstance {
  addClass(field: string, className: string): Promise<void>;
  clear(field: string): Promise<void>;
  focus(field: string): Promise<void>;
  getState(): IHostedFieldsState;
  on(
    event: string,
    callback: (data: IHostedFieldsEventData) => void
  ): IHostedFieldsInstance;
  off(
    event: string,
    callback: (data: IHostedFieldsEventData) => void
  ): IHostedFieldsInstance;
  removeAttribute(options: { field: string; attribute: string }): Promise<void>;
  removeClass(field: string, className: string): Promise<void>;
  setAttribute(options: {
    field: string;
    attribute: string;
    value: string;
  }): Promise<void>;
  setMessage(options: { field: string; message: string }): void;
  setMonthOptions(options: unknown[]): void;
  setPlaceholder(field: string, placeholder: string): Promise<void>;
  tokenize(options?: {
    vault?: boolean;
    cardholderName?: string;
    billingAddress?: unknown;
  }): Promise<IHostedFieldsTokenizePayload>;
  teardown(): Promise<void>;
}

/**
 * Hosted Fields field configuration
 */
interface IHostedFieldConfig {
  container: string | HTMLElement;
  placeholder?: string;
  type?: string;
  formatInput?: boolean;
  maskInput?: boolean | { character?: string; showLastFour?: boolean };
  select?: boolean | { options: string[] };
  maxCardLength?: number;
  minlength?: number;
  maxlength?: number;
  prefill?: string;
  rejectUnsupportedCards?: boolean;
  supportedCardBrands?: Record<string, boolean>;
}

/**
 * Hosted Fields create options
 */
interface IHostedFieldsCreateOptions {
  client?: IBraintreeClient;
  authorization?: string;
  fields: {
    number?: IHostedFieldConfig;
    cvv?: Omit<IHostedFieldConfig, "formatInput" | "maxCardLength">;
    expirationDate?: Pick<
      IHostedFieldConfig,
      "container" | "placeholder" | "type" | "select" | "prefill"
    >;
    expirationMonth?: Pick<
      IHostedFieldConfig,
      "container" | "placeholder" | "type" | "select" | "prefill"
    >;
    expirationYear?: Pick<
      IHostedFieldConfig,
      "container" | "placeholder" | "type" | "select" | "prefill"
    >;
    postalCode?: Pick<
      IHostedFieldConfig,
      | "container"
      | "placeholder"
      | "type"
      | "minlength"
      | "maxlength"
      | "prefill"
    >;
    cardholderName?: Pick<
      IHostedFieldConfig,
      | "container"
      | "placeholder"
      | "type"
      | "minlength"
      | "maxlength"
      | "prefill"
    >;
  };
  styles?: {
    input?: Record<string, string>;
    ":focus"?: Record<string, string>;
    ".valid"?: Record<string, string>;
    ".invalid"?: Record<string, string>;
    [key: string]: Record<string, string> | undefined;
  };
  preventAutofill?: boolean;
}

