// ============================================================================
// Apple Pay Types (Simplified - avoids external namespace dependency)
// ============================================================================

/**
 * Apple Pay line item
 */
interface IApplePayLineItem {
  label: string;
  amount: string;
  type?: "final" | "pending";
}

/**
 * Apple Pay shipping method
 */
interface IApplePayShippingMethod {
  label: string;
  detail?: string;
  amount: string;
  identifier: string;
}

/**
 * Apple Pay payment contact
 */
interface IApplePayPaymentContact {
  phoneNumber?: string;
  emailAddress?: string;
  givenName?: string;
  familyName?: string;
  addressLines?: string[];
  locality?: string;
  postalCode?: string;
  administrativeArea?: string;
  country?: string;
  countryCode?: string;
}

/**
 * Apple Pay payment method
 */
interface IApplePayPaymentMethod {
  displayName?: string;
  network?: string;
  type?: "debit" | "credit" | "prepaid" | "store" | "eMoney";
  billingContact?: IApplePayPaymentContact;
}

/**
 * Apple Pay payment token
 */
interface IApplePayPaymentToken {
  paymentMethod: IApplePayPaymentMethod;
  transactionIdentifier: string;
  paymentData: Record<string, unknown>;
}

/**
 * Apple Pay payment request
 */
interface IApplePayPaymentRequest {
  countryCode: string;
  currencyCode: string;
  supportedNetworks: string[];
  merchantCapabilities: Array<
    "supports3DS" | "supportsEMV" | "supportsCredit" | "supportsDebit"
  >;
  total: IApplePayLineItem;
  lineItems?: IApplePayLineItem[];
  requiredBillingContactFields?: Array<
    "email" | "name" | "phone" | "postalAddress"
  >;
  requiredShippingContactFields?: Array<
    "email" | "name" | "phone" | "postalAddress"
  >;
  shippingMethods?: IApplePayShippingMethod[];
  shippingType?: "shipping" | "delivery" | "storePickup" | "servicePickup";
  applicationData?: string;
}

/**
 * Apple Pay tokenize payload
 */
interface IApplePayTokenizePayload {
  nonce: string;
  type: string;
  description: string;
  details: {
    cardType: string;
    cardholderName?: string;
    dpanLastTwo: string;
    rawCardType: string;
    [key: string]: unknown;
  };
  binData: {
    commercial: string;
    countryOfIssuance: string;
    debit: string;
    durbinRegulated: string;
    healthcare: string;
    issuingBank: string;
    payroll: string;
    prepaid: string;
    productId: string;
  };
  consumed: boolean;
}

/**
 * Apple Pay instance
 */
interface IApplePayInstance {
  merchantIdentifier: string;
  createPaymentRequest(
    options: Partial<IApplePayPaymentRequest>
  ): IApplePayPaymentRequest;
  performValidation(options: {
    validationURL: string;
    displayName?: string;
  }): Promise<unknown>;
  tokenize(options: {
    token: IApplePayPaymentToken;
  }): Promise<IApplePayTokenizePayload>;
  teardown(): Promise<void>;
}

